import { db } from "../../../../firebase/connection.js";
import { collection, addDoc, Timestamp } from 'firebase/firestore'
import { useState } from "react";

const Create =() => {

  const usersCollectionRef = collection(db, "notas");
  const [title, setTitle] = useState([""])
  const [description, setDescription] = useState([""])

  //guardar nota
  const saveNote = (event) =>{
    event.preventDefault()
    addDoc(usersCollectionRef,{
      title:title, description:description, created:Timestamp.now()
    })
    setTitle("");
    setDescription("");
  }

  /*const saveNote2 = async (e) => {
    e.preventDefault()
    try {
      await addDoc(collection(db, 'newNote'), {
        title: title,
        description: description,
        created: Timestamp.now()
      })

    } catch (err) {
      alert(err)
    }
  }*/

  return (
    <div>

      <form onSubmit={saveNote} className='addTask' name='addTask'>
        <input 
          type='text' 
          name='title' 
          onChange={(event) => setTitle(event.target.value.toUpperCase())} 
          value={title}
          placeholder='Enter title'/>
        <textarea 
          onChange={(event) => setDescription(event.target.value)}
          placeholder='Enter task decription'
          value={description}></textarea>
        <button type='submit'>Done</button>
      </form> 

      </div>

  )


}

export default Create;


