import {useState} from 'react'
//import TaskItem from './TaskItem'
import EditTask from './Crud/Edit.js'
import { doc, updateDoc, deleteDoc} from "firebase/firestore";
import {db} from '../../../firebase/connection.js'

function Task({id, completed, toEditTitle,toEditDescription,onClose,edit}) {
///////
const [title, setTitle] = useState(toEditTitle)
  const [description, setDescription] = useState(toEditDescription)

  // function to update firestore 
  const handleUpdate = async (e) => {
    e.preventDefault()
    const taskDocRef = doc(db, 'notas', id)
    try{
      await updateDoc(taskDocRef, {
        title: title,
        description: description
      })
      onClose()
    } catch (err) {
      alert(err)
    }

  }

//////////
  const [checked, setChecked] = useState(completed)
  const [open, setOpen] = useState({edit:false, view:false})

  const handleClose = () => {
    setOpen({edit:false, view:false})
  }

  /* function to update firestore */
  const handleChange = async () => {
    const taskDocRef = doc(db, 'notas', id)
    try{
      await updateDoc(taskDocRef, {
        completed: checked
      })
    } catch (err) {
      alert(err)
    }
  }

  /* function to delete a document from firstore */ 
  const handleDelete = async () => {
    const taskDocRef = doc(db, 'notas', id)
    try{
      await deleteDoc(taskDocRef)
    } catch (err) {
      alert(err)
    }
  }


  return (
    <div className={`task ${checked && 'task--borderColor'}`}>
      <div className='task__body'>
        <input value={title}/>
        <br/>
        <input value={description}/>

        <div className='task__buttons'>
          <div className='task__deleteNedit'>
            <button 
              className='task__editButton' 
              onClick={() => setOpen({...open, edit : true})}>
              Editar
            </button>
            <button className='task__deleteButton' onClick={handleDelete}>Delete</button>

          </div>
        </div>
        <br/>
      </div>

{open.edit &&
        <Edit
          onClose={handleClose} 
          toEditTitle={title} 
          toEditDescription={description} 
          open={open.edit}
          id={id} />
      }
    </div>
  )
}

export default Task


