//import Modal from "./Modal"
import {useState} from 'react'
import { doc, updateDoc } from "firebase/firestore";
import {db} from '../../../../firebase/connection.js'
import Swal from 'sweetalert2'


function EditTask({open, onClose, toEditTitle, toEditDescription, id}) {

  const [title, setTitle] = useState(toEditTitle)
  const [description, setDescription] = useState(toEditDescription)

  // function to update firestore 
  const handleUpdate = async (e) => {
    e.preventDefault()
    const taskDocRef = doc(db, 'notas', id)
    try{
      await updateDoc(taskDocRef, {
        title: title,
        description: description
      })
      onClose()
    } catch (err) {
      alert(err)
    }

  }
  /////////////

  Swal
    .fire({
        title: "Tu nombre",
        input: "text",

        value:"title",
        showCancelButton: true,
        onSubmit:{handleUpdate},
        confirmButtonText: "handleUpdate",
        cancelButtonText: "Cancelar",
    })
    .then(handleUpdate => {
        if (handleUpdate.value) {
            let title = handleUpdate.value;
            <input type='text' name='title' onChange={(e) => setTitle(e.target.value.toUpperCase())} value={title}/>

            console.log("Hola, " + title);
        }
    });
  /////

  return (

    <form onSubmit={handleUpdate} className='editTask'>
    <input type='text' name='title' onChange={(e) => setTitle(e.target.value.toUpperCase())} value={title}/>
    <textarea onChange={(e) => setDescription(e.target.value)} value={description}></textarea>
    <button type='submit'>Editar</button>
  </form> 
  )

}

export default EditTask




