import { db } from "../../../../firebase/connection.js";
import { collection, addDoc, Timestamp } from 'firebase/firestore'
import { useState } from "react";
import "./Style.css";

import Read from "./Read.js";
const Create =() => {

  const usersCollectionRef = collection(db, "notas");
  const [title, setTitle] = useState([""])
  const [description, setDescription] = useState([""])

  //guardar nota
  const saveNote = (event) =>{
    event.preventDefault()
    addDoc(usersCollectionRef,{
      title:title, description:description, created:Timestamp.now()
    })
    setTitle("");
    setDescription("");
  }

  return (
    <div className='createStyleuno'>

      <form onSubmit={saveNote} className='createStyle'>
        <input 
          type='text'
          value={title}
          placeholder='Titulo'
          onChange={(event) => setTitle(event.target.value.toUpperCase())}   
        />
        <br/>
        <textarea 
          value={description}
          placeholder='Nota'
          onChange={(event) => setDescription(event.target.value)}>
        </textarea>
        <br/>
        <button type='submit'>Done</button>
      </form> 

      <div className="uno">
        <div className="dos">
        <Read/>
        <button 
              >
              Edit
            </button>
        </div>
      </div>
    </div>
  )
}
export default Create;


