//import { StatusBar } from 'expo-status-bar';
//import { StyleSheet, Text, View } from 'react-native';
//import { TextInput } from 'react-native-web';
import { React,useState } from "react";
import { collection, doc, setDoc, addDoc, updateDoc, deleteDoc, getDoc, getDocs, where, query } from "firebase/firestore"; 
import { db } from '../../../firebase/connection.js';

export default function Home() {

  const [username, setName] = useState(''); 
  const [email, setEmail] = useState('');

     function create () {
        setDoc(doc(db, "users", 'LA'), {     
          username: username,
          email: email,
          }).then(() => { 
        // Data saved successfully!
        console.log('data submitted');  

        }).catch((error) => { 
            // The write failed...
          console.log(error);
      });


    };

    function getSepcificDataWithID () {
          getDoc(doc(db, "users", 'IMx2OXMCR0WD7upXNcKq')).then(docData => { 
      // Data saved successfully!

      if (docData.exists()) {

        // console.log(docData.data());

        setName(docData.data().username);
        setEmail(docData.data().email);   

      } else {
         console.log('No such a data!');
      }

    }).catch((error) => {
          // The write failed...
          console.log(error);
    })
    }

    function update() {
    updateDoc(doc(db, "users", 'LA'), {     
      username: username,
      email: email,
    }).then(() => { 
      // Data saved successfully!
      console.log('data submitted');  

    }).catch((error) => {
          // The write failed...
          console.log(error);
    });
    }

    function deleteData() {
          deleteDoc(doc(db, "users", 'LA'));    
    }

    function getAlldata() {
      getDocs(collection(db, "users")).then(docSnap => {
        let users = [];
        docSnap.forEach((doc)=> {
            users.push({ ...doc.data(), id:doc.id })
        });
            console.log("Document data:", users);       
      });
    }

    function getDataWithQuery () {
      getDocs(query(collection(db, "users"), where('email','==', 'NewUser@gmail.com'))).then(docSnap => {
         let users = []; 
          docSnap.forEach((doc)=> {
          users.push({ ...doc.data(), id:doc.id })
      });
          console.log("Document data:", users[0].username);           
      });
    }

  return (
    <div style={styles.container}>
      <p>Firebase crud!</p> 

      <input value={username} onChangeText={(username) => {setName(username)}} placeholder="Username" style={styles.textBoxes}></input>
      <input value={email} onChangeText={(email) => {setEmail(email)}} placeholder="Email" style={styles.textBoxes}></input>

      <button onClick={create}>Refresh Data </button>      

    </div>
  );
}

const styles = StyleSheet.create({
  container: {  
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  textBoxes: {
    width: '90%', 
    fontSize: 18,
     padding: 12,
      borderColor: 'gray', 
    borderWidth: 0.2,
     borderRadius: 10
  }   
});
