import {useState} from 'react'
//import TaskItem from './TaskItem'
import EditTask from './Crud/Edit.js'
import { doc, updateDoc, deleteDoc} from "firebase/firestore";
import {db} from '../../../firebase/connection.js'

import React from 'react';
import ReactDOM from 'react-dom';
import Modal from 'react-modal';
function Task({id, title, description, completed, toEditTitle,toEditDescription}) {
///////
const [modalIsOpen, setIsOpen] = React.useState(false);

function openModal() {
  setIsOpen(true);
}
 function closeModal() {
  setIsOpen(false);
}
///////
/*


function EditTask({open, onClose, toEditTitle, toEditDescription, id}) {
  let subtitle;


  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = '#f00';
  }


  /////////////

  return (
    <div>

    <Modal
      isOpen={modalIsOpen}
      onAfterOpen={afterOpenModal}
      contentLabel="Example Modal"
    >

    </Modal>
  </div>
);


}
*/
//////////
  const [checked, setChecked] = useState(completed)
  const [open, setOpen] = useState({edit:false, view:false})

  const handleClose = () => {
    setOpen({edit:false, view:false})
  }

  /* function to delete a document from firstore */ 
  const handleDelete = async () => {
    const taskDocRef = doc(db, 'notas', id)
    try{
      await deleteDoc(taskDocRef)
    } catch (err) {
      alert(err)
    }
  }
///

///
  return (
    <div className={`task ${checked && 'task--borderColor'}`}>
      <div className='task__body'>
       <h2>{title}</h2>
        <p>{description}</p>
        <div className='task__buttons'>
          <div className='task__deleteNedit'>
          <button onClick={openModal}>Open Modal</button>

            <button className='task__deleteButton' onClick={handleDelete}>Delete</button>

          </div>
        </div>
        <br/>
      </div>

{openModal &&
        <EditTask 
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        toEditTitle={title} 
          toEditDescription={description} 
          open={open.edit}
          id={id} />
      }
    </div>
  )
}


export default Task


