//import Modal from "./Modal"
import {useState} from 'react'
import { doc, updateDoc } from "firebase/firestore";
import {db} from '../../../../firebase/connection.js'
//import Swal from 'sweetalert2'
import swal from 'sweetalert';
import React, { Component } from 'react';
import ReactDOM from 'react-dom';

function EditTask({open, onClose, toEditTitle, toEditDescription, id}) {

  const [title, setTitle] = useState(toEditTitle)
  const [description, setDescription] = useState(toEditDescription)

  // function to update firestore 
  const handleUpdate = async (e) => {
    e.preventDefault()
    const taskDocRef = doc(db, 'notas', id)
    try{
      await updateDoc(taskDocRef, {
        title: title,
        description: description
      })
      onClose()
    } catch (err) {
      alert(err)
    }

  }
  /////////////


  const DEFAULT_INPUT_TEXT = "";

  class MyInput extends Component {
    constructor(props) {
      super(props);

      this.state = {
        text: DEFAULT_INPUT_TEXT,
      };
    }

    changeText(e) {
      let text = e.target.value;

      this.setState({
        text,
      });
      swal.setActionValue(text);
    }

    render() {
      return (
        <input
          value={this.state.text}
          onChange={this.changeText.bind(this)}
        />
      )
    }
  }

  // We want to retrieve MyInput as a pure DOM node: 
  let wrapper = document.createElement('div');
  ReactDOM.render(<MyInput />, wrapper);
  let el = wrapper.firstChild;

  swal({
    text: "Write something here:",
    content: el,
    buttons: {
      confirm: {
        /*
         * We need to initialize the value of the button to
         * an empty string instead of "true":
         */
        value: DEFAULT_INPUT_TEXT,
      },
    },
  })
  .then((value) => {
    swal(`You typed: ${value}`);
  });
  /////

  return (

      <form onSubmit={handleUpdate} className='editTask'>
      <input type='text' name='title' onChange={(e) => setTitle(e.target.value.toUpperCase())} value={title}/>
      <textarea onChange={(e) => setDescription(e.target.value)} value={description}></textarea>
      <button type='submit'>Editar</button>
    </form> 


  )

}

export default EditTask




