//import Modal from "./Modal"
import {useState} from 'react'
import { doc, updateDoc } from "firebase/firestore";
import {db} from '../../../../firebase/connection.js'
import Swal from 'sweetalert2'


function EditTask({open, onClose, toEditTitle, toEditDescription, id}) {

  const [title, setTitle] = useState(toEditTitle)
  const [description, setDescription] = useState(toEditDescription)

  // function to update firestore 
  const handleUpdate = async (e) => {
    e.preventDefault()
    const taskDocRef = doc(db, 'notas', id)
    try{
      await updateDoc(taskDocRef, {
        title: title,
        description: description
      })
      onClose()
    } catch (err) {
      alert(err)
    }

  }
  /////////////
  const { value: formValues } =  Swal.fire({
    title: 'Multiple inputs',
    html:
      '<input id="title" class="swal2-input">' +
      '<input id="swal-input2" class="swal2-input">',
    focusConfirm: false,
    preConfirm: () => {
      return [
       // document.getElementById('swal-input1').value,
        //document.getElementById('swal-input2').value
        <form onSubmit={handleUpdate} className='editTask'>
        <input id='title' type='text' name='title' onChange={(e) => setTitle(e.target.value.toUpperCase())} value={title}/>
        <textarea id='swal-input2' onChange={(e) => setDescription(e.target.value)} value={description}></textarea>
        <button  type='submit'>Editar</button>
      </form> 
      ]
    }
  })

  if (formValues) {
    Swal.fire(JSON.stringify(formValues))

    const handleUpdate = async (e) => {
      e.preventDefault()
      const taskDocRef = doc(db, 'notas', id)
      try{
        await updateDoc(taskDocRef, {
          title: title,
          description: description
        })
        onClose()
      } catch (err) {
        alert(err)
      }

    }
  }
  /////
  /*
  return (
      <form onSubmit={handleUpdate} className='editTask'>
        <input type='text' name='title' onChange={(e) => setTitle(e.target.value.toUpperCase())} value={title}/>
        <textarea onChange={(e) => setDescription(e.target.value)} value={description}></textarea>
        <button type='submit'>Editar</button>
      </form> 
  )
  */
}

export default EditTask
